package week2;

import java.util.HashMap;
import java.util.Map;

public class FindTheDifference {

    public static void main(String[] args) {
        System.out.println(findTheDifference("", ""));
    }

    public static char findTheDifference(String s, String t) {
        Map<Character, Integer> stringsMap = new HashMap<>();
        Character i;
        if(s.length() < t.length()) {
            i = findDifference(s, t, stringsMap);
        } else {
            i = findDifference(t, s, stringsMap);
        }
        if (i != null) return i;

        return '0';
    }

    private static Character findDifference(String s, String t, Map<Character, Integer> stringsMap) {

        for(int i = 0; i < s.length(); i++) {
            if(stringsMap.get(s.charAt(i)) == null) {
                stringsMap.put(s.charAt(i),1);
            } else {
                int count = stringsMap.get(s.charAt(i));
                stringsMap.put(s.charAt(i), ++count);
            }
        }

        for(int i = 0; i < t.length(); i++) {
            if(stringsMap.get(t.charAt(i)) == null) {
                return t.charAt(i);
            } else {
                int count = stringsMap.get(t.charAt(i));
                stringsMap.put(t.charAt(i), ++count);
            }
        }

        for(Map.Entry<Character, Integer> result: stringsMap.entrySet()) {
            if(result.getValue() % 2 != 0) {
                return result.getKey();
            }
        }
        return null;
    }
}
